<?php// sp.php â€” Create a new shipment and generate tracking numberinclude 'includes/db_connect.php';// Generate a unique tracking number$tracking_no = 'IC' . strtoupper(substr(md5(uniqid()), 0, 8));if ($_SERVER['REQUEST_METHOD'] === 'POST') {    $customer_name = trim($_POST['customer_name'] ?? '');    $origin = trim($_POST['origin'] ?? '');    $destination = trim($_POST['destination'] ?? '');    $service = trim($_POST['service'] ?? '');    $package = trim($_POST['package'] ?? '');    $package_weight = trim($_POST['package_weight'] ?? '');    $declared_value = trim($_POST['declared_value'] ?? '');    $additional_services = trim($_POST['additional_services'] ?? '');    $third_party_account = trim($_POST['third_party_account'] ?? '');    $status = 'In warehouse';    // Insert into database    $stmt = $db->prepare("        INSERT INTO shipments (            tracking_no, customer_name, origin, destination, service,            package, package_weight, declared_value, additional_services,            third_party_account, status        )        VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)    ");    $stmt->execute([        $tracking_no, $customer_name, $origin, $destination, $service,        $package, $package_weight, $declared_value, $additional_services,        $third_party_account, $status    ]);    echo "<!DOCTYPE html>    <html lang='en'>    <head><meta charset='UTF-8'><title>Shipment Created</title></head>    <body style='font-family: Arial, sans-serif; margin: 40px;'>        <h2>Shipment Created</h2>        <p>Your tracking number is: <strong>$tracking_no</strong></p>        <p><a href='tracking.php'>Track a shipment</a> |            <a href='admin.php'>Admin Dashboard</a></p>    </body></html>";    exit;}?><!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <title>Create Shipment</title></head><body style="font-family: Arial, sans-serif; margin: 40px;">    <h1>Create New Shipment</h1>    <form method="POST">        <label>Customer Name:</label><br>        <input type="text" name="customer_name" required><br><br>        <label>Origin:</label><br>        <input type="text" name="origin" required><br><br>        <label>Destination:</label><br>        <input type="text" name="destination" required><br><br>        <label>Service:</label><br>        <input type="text" name="service" required><br><br>        <label>Package:</label><br>        <input type="text" name="package" required><br><br>        <label>Package Weight:</label><br>        <input type="text" name="package_weight" required><br><br>        <label>Declared Value:</label><br>        <input type="text" name="declared_value"><br><br>        <label>Additional Services:</label><br>        <input type="text" name="additional_services"><br><br>        <label>Third Party Account Number:</label><br>        <input type="text" name="third_party_account"><br><br>        <button type="submit">Create Shipment</button>    </form></body></html>